class Price
{
    private String item;
    private int price;
    public Price (String itm, int pr)
    {
        this.item = itm;
        this.price = pr;
    }
   
    public String getItem ()
    {
        return item;
    }
    public void setItem (String item)
    {
        this.item = item;
    }
    public int getPrice ()
    {
        return price;
    }
    public void setPrice (int price)
    {
        this.price = price;
    }}
